function onWindowLoad() {
	let a = localStorage.getItem("access_token");

	function b() {
		var a = new Date;
		return a.getMonth() + 1 + "/" + a.getDate() + "/" + a.getFullYear()
	}
	a && fetch("https://yudiz.keka.com/k/api/mytime/attendance/summary", {
		headers: {
			accept: "application/json, text/plain, */*",
			"accept-language": "en-US,en;q=0.9",
			authorization: "Bearer " + a,
			"cache-control": "no-cache",
			"content-type": "application/json; charset=utf-8",
			pragma: "no-cache",
			"request-context": "appId=cid-v1:Keka Angular SPA",
			"request-id": "|12ac751a9c54457e90777fd3396381ef.91225a2de2ba495d",
			"sec-ch-ua": '"Chromium";v="92", " Not A;Brand";v="99", "Google Chrome";v="92"',
			"sec-ch-ua-mobile": "?0",
			"sec-fetch-dest": "empty",
			"sec-fetch-mode": "cors",
			"sec-fetch-site": "same-origin",
			traceparent: "00-12ac751a9c54457e90777fd3396381ef-91225a2de2ba495d-01",
			"x-requested-with": "XMLHttpRequest"
		},
		referrer: "https://yudiz.keka.com/",
		referrerPolicy: "strict-origin-when-cross-origin",
		body: null,
		method: "GET",
		mode: "cors",
		credentials: "include"
	}).then(a => a.json()).then(a => {
		if (a[a.length - 1].effectiveHoursInHHMM) {
			let o = a[a.length - 1].breakDurationInHHMM,
				p = a[a.length - 1].shiftPolicyName,
				q = a[a.length - 1].arrivalMessage;
			if (console.log({
					shift: p,
					break: o,
					arrival: q
				}), 0 != a[a.length - 1].originalTimeEntries.length) {
				let i = a[a.length - 1].effectiveHoursInHHMM.replace("h", "").replace("m", "").replace(" ", ":") + ":00",
					e = a[a.length - 1].originalTimeEntries,
					r = e[e.length - 1].actualTimestamp.split("T")[1];
				if (0 === e[e.length - 1].originalPunchStatus) {
					let s = new Date(b() + " " + i),
						t = new Date(b() + " " + r),
						u = new Date,
						v = new Date(b() + " 08:00:00").getTime() - s.getTime();
					diff2 = u.getTime() - t.getTime();
					let h = v - diff2;
					if (console.log(b() + " " + i), h <= 0) console.log("🎉 You can go Home Now! 🤙🏻");
					else {
						var f = h,
							j = Math.floor(f / 1e3 / 60 / 60),
							k = Math.floor((f -= 1e3 * j * 3600) / 1e3 / 60),
							l = Math.floor((f -= 1e3 * k * 60) / 1e3);
						f -= 1e3 * l;
						var m, g, d, n, c = new Date;
						c.setHours(c.getHours() + j), c.setMinutes(c.getMinutes() + k), c.setSeconds(c.getSeconds() + l), console.log("\u{1F552} Your 8 hours complete at " + (g = (m = c).getHours(), d = m.getMinutes(), n = g >= 12 ? "PM" : "AM", (g = (g %= 12) || 12) + ":" + (d = d < 10 ? "0" + d : d) + " " + n) + "\u{1F552}")
					}
				} else console.log("\u{1F552} Please wait for keka to update ! \u{1F552}")
			}
		}
	}).catch(function(a) {
		console.error(a)
	})
}
onWindowLoad()
